import { useEffect, useState } from "react";
import { Button, Table } from "reactstrap";
import { Link } from "react-router-dom";
import { deactivatePatron, getPatrons } from "../data/patronsData";

export default function PatronList() {
  const [patrons, setPatrons] = useState([]);

  useEffect(() => {
    getPatrons().then(setPatrons);
  }, []);

  const handleButtonClick = (id) => {
    deactivatePatron(id)
      .then((data) => {
        // Handle success, if needed
        console.log("Patron deactivated successfully:", data);
      })
  };


  return (
    <div className="container">
      <div className="sub-menu bg-light">
        <h4>Patrons</h4>
      </div>
      <Table>
        <thead>
          <tr>
            <th>Id</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Address</th>
            <th>Active Member</th>
          </tr>
        </thead>
        <tbody>
          {patrons.map((p) => (
            <tr key={`patrons-${p.id}`}>
              <th scope="row">{p.id}</th>
              <td>{p.firstName}</td>
              <td>{p.lastName}</td>
              <td>{p.email}</td>
              <td>{p.address}</td>
              <td>{p.isActive ? "Yes" : "No"}</td>
              <td>
                {
                 <Button onClick={() => handleButtonClick(p.id)}>Deactivate</Button>
                }
              </td>
              <td>
                <Link to={`${p.id}`}>Details</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}
