import { useEffect, useState } from "react";
import { Button, Table } from "reactstrap";
import { Link } from "react-router-dom";
import { getAvaialbleMaterials } from "../../data/materialsData";
import { createCheckout } from "../../data/checkoutsData";

export default function AvailableMaterialList() {
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    getAvaialbleMaterials().then(setMaterials);
  }, []);

  const handleButtonClick = (id) => {
    console.log("Removing material with ID:", id);
    createCheckout(id)
    .then(() => {
      // Update the state to reflect the removal
      setMaterials((prevMaterials) => prevMaterials.filter(material => material.id !== id));
    })
  };

  return (
    <div className="container">
      <div className="sub-menu bg-light">
        <h4>Materials</h4>
        <Link to="/materials/create">Add</Link>
      </div>
      <Table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Title</th>
            <th>Type</th>
            <th>Genre</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {materials.map((m) => (
            <tr key={`materials-${m.id}`}>
              <th scope="row">{m.id}</th>
              <td>{m.materialName}</td>
              <td>{m.materialType.name}</td>
              <td>{m.genre.name}</td>
              <td>
              {m.outOfCirculationSince == null && (
                  <Button onClick={() => handleButtonClick(m.id)}>
                    Remove
                  </Button>
                )}
              </td>
              <td>
                <Link to={`${m.id}`}>Details</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}
